mapper.select:
SELECT

FROM property_manager_info
WHERE pm_id = #{pmId}

	AND state = #{state}

ORDER BY id DESC LIMIT 1

mapper.update:
UPDATE property_manager_info
pm_id = #{pmId}, funds_holder = #{isFundsHolder}, number_of_payments = #{numberOfPayments}, payment_amount = #{paymentAmount}, payment_type = #{paymentType}, state = #{state}, commission = #{commission}, net_rate = #{isNetRate}
WHERE id = #{id}

UPDATE property_manager_info
SET state = 'Suspended'
WHERE id = #{id}
