mapper.select:
SELECT

FROM manager_to_channel


		property_manager_id = #{propertyManagerId}


		AND channel_partner_id = #{channelPartnerId}


ORDER BY id DESC LIMIT 1

SELECT

FROM manager_to_channel
WHERE channel_partner_id = #{channelPartnerId}

	AND net_rate = #{netRate}

mapper.update:
UPDATE manager_to_channel
property_manager_id = #{propertyManagerId}, channel_partner_id = #{channelPartnerId}, channel_partner_commission = #{channelPartnerCommission}, funds_holder = #{isFundsHolder}, net_rate = #{isNetRate}
WHERE id = #{id}
