mapper.select:
SELECT

FROM party
WHERE name = #{name}

	AND user_type = #{userType}


	AND state = #{state}

SELECT

FROM party
WHERE email_address = #{emailAddress}

	AND user_type = #{userType}


	AND name = #{name}


	AND state = #{state}


SELECT

FROM party p JOIN channel_partner cp ON p.id = cp.party_id
WHERE cp.channel_name LIKE #{channelNameStartsWith}
AND p.user_type = #{userType}
AND p.state IN ('Created', 'Suspended')

mapper.update:
UPDATE party
name = #{name}, state = #{state}, postal_address = #{postalAddress}, email_address = #{emailAddress}, mobile_phone = #{mobilePhone}, password = #{password}, currency = #{currency}, user_type = #{userType}
WHERE id = #{id}

UPDATE party
SET state = 'Final'
WHERE id = #{id}
